import base64def encrypt_text(text, rounds):    for _ in range(rounds):        encrypted_bytes = base64.b64encode(text.encode())        encrypted_text = encrypted_bytes.decode()        text = encrypted_text        print(f"Aşama {_+1}: {text}")    return textdef decrypt_text(text, rounds):    for _ in range(rounds):        decrypted_bytes = base64.b64decode(text.encode())        decrypted_text = decrypted_bytes.decode()        text = decrypted_text        print(f"Aşama {_+1}: {text}")    return text# Ana döngüwhile True:    print("1. Metni Şifrele")    print("2. Şifreli Metni Çöz")    print("0. Çıkış")    choice = input("Bir seçenek girin (0-2): ")    if choice == "1":        text = input("Metni girin: ")        rounds = int(input("Kaç kez şifreleneceğini girin: "))        encrypted_text = encrypt_text(text, rounds)        print("Şifrelenmiş Metin: ", encrypted_text)        print()    elif choice == "2":        text = input("Şifreli metni girin: ")        rounds = int(input("Kaç kez çözümleneceğini girin: "))        decrypted_text = decrypt_text(text, rounds)        print("Çözümlenmiş Metin: ", decrypted_text)        print()    elif choice == "0":        break    else:        print("Geçersiz seçenek! Lütfen tekrar deneyin.")        print()